import React, {Component} from 'react';
import TodoItem from './TodoItem';
import TodoForm from './TodoForm';
import * as apiCalls from './api';
const APIURL = '/api/todos/';

class TodoList extends Component{
  constructor(props) {
    super(props);
    this.state= {
      todos:[]
    }
    this.addTodo = this.addTodo.bind(this);
  }
  componentWillMount() {
    this.loadTodos();
  }

  async loadTodos() {
    let todos = await apiCalls.getTodos();
    this.setState({todos});
  }

  async addTodo(val) {
    let newTodo = await apiCalls.createTodo(val);
    this.setState({
      todos: [...this.state.todos, newTodo]
    })    
  }

  async deleteTodo(id) {
    await apiCalls.removeTodo(id);
    const todos = this.state.todos.filter(todo => todo._id !== id);
    this.setState({
        todos: todos
    });
  }

  toggleTodo(todo) {
    const updateUrl = APIURL + todo._id;
    fetch(updateUrl, {
      method: 'put', //the default method is get
      headers: new Headers({
        'Content-Type': 'application/json',
      }),
      body: JSON.stringify({completed:!todo.completed})
    })
    .then(resp => {
      if (!resp.ok) {
        if (resp.status >= 400 && resp.status < 500) {
          return resp.json().then(data => {     //resp.json will be the error message
            let err = {errorMessage: data.message};
            throw err;
          })
        } else {
          let err = {errorMessage: 'Please try again later. Server is not responding'};
          throw err;
        }
      }
      return resp.json()    
    })
    .then((updatedTodo) => {
      const todos = this.state.todos.map(t => 
        (t._id === updatedTodo._id)
        ? {...t, completed: !t.completed}
        : t
      );
      this.setState({
        todos: todos
      });
    })
  }

  render (){
    const todos = this.state.todos.map(t => (
      <TodoItem 
        key={t._id}
        {...t}
        onDelete={this.deleteTodo.bind(this, t._id)} //must be bound here and not on top since id has to be passed in
        onToggle={this.toggleTodo.bind(this, t)}
      />
    ));
    return(
      <div>
         <h1>Todo List</h1>
         <TodoForm addTodo={this.addTodo}/>
         <ul>{todos}</ul>        
       </div>
    );
  }
}

export default TodoList;
